import React,{useState,useEffect} from 'react'
import "./Nav.css";
function Nav() {
    const [show , handleShow] = useState(false);
    useEffect(() => {
         window.addEventListener("scroll",()=>{
             if(window.scrollY > 100){
                  handleShow(true);
             }
             else handleShow(false);
         });
         return () =>{
             window.removeEventListener("scroll");
         };
    }, [])
    return (
        <div className={`nav ${show&& "nav_black"}`}>
            <img className ="nav_logo"  src ="https://afastation.sfo2.digitaloceanspaces.com/wp-content/uploads/2021/03/04145406/493f5bba-81a4-11e9-bf79-066b49664af6_cm_1440w.png" alt="Netflix Logo"
            />
             <img className ="nav_avatar"  src ="https://ih0.redbubble.net/image.618427277.3222/flat,1000x1000,075,f.u2.jpg" alt="Netflix Logo"
            />
        </div>
    )
}

export default Nav
